SealSecurity.Script:
@{
    SecurityUser user = Model;
	//Basic authentication script: use the user name and password to authenticate the user and set his name and security groups...
	//user.WebUserName -> user name from the login screen
	//user.WebPassword -> password from the login screen

	user.Name = user.WebUserName; //Display name
    //security check can be hardcoded
	if (string.IsNullOrEmpty(user.WebUserName)) {
		user.AddDefaultSecurityGroup();
	}
	else if (user.WebUserName == "ex" && user.WebPassword == "ex")
	{
		user.AddSecurityGroup("Group (Execute)");
	}
	else if (user.WebUserName == "exo" && user.WebPassword == "exo")
	{
		user.AddSecurityGroup("Group (Execute Output)");
	}
	else if (user.WebUserName == "sc" && user.WebPassword == "sc")
	{
		user.AddSecurityGroup("Group (Schedule)");
	}
	else if (user.WebUserName == "ed" && user.WebPassword == "ed")
	{
		user.AddSecurityGroup("Group (Edit)");
	}
	else if (user.WebUserName == "exed" && user.WebPassword == "exed")
	{
		user.AddSecurityGroup("Group (Execute)");
		user.AddSecurityGroup("Group (Edit)");
	}
	else if (user.WebUserName == "sc1" && user.WebPassword == "sc1")
	{
		user.AddSecurityGroup("Group (Scripts 1)");
	}
	else if (user.WebUserName == "sc2" && user.WebPassword == "sc2")
	{
		user.AddSecurityGroup("Group (Scripts 1)");
		user.AddSecurityGroup("Group (Scripts 2)");
	}
	else if (user.WebUserName == "demo" && user.WebPassword == "demo")
	{
		user.AddSecurityGroup("Group (Demo)");
	}
	else if (user.WebUserName == "nogroup" && user.WebPassword == "nogroup")
	{
        throw new Exception("This user has no group");
	}
	else if (user.WebUserName == "ben" && user.WebPassword == "ben")
	{
        user.Name = "Benjaminto Seal";
		user.AddDefaultSecurityGroup();
        var imagePath = @"C:\temp\" + user.WebUserName + ".png";
        if (System.IO.File.Exists(imagePath)) {
            user.Tag = Seal.Helpers.Helper.HtmlMakeImageSrcData(imagePath);
        }
	}
}

SecurityGroup.FoldersScript:
@{
    SecurityUser user = Model;
    //Full documentation at https://sealreport.org/Help/Index.html
    
    if (user.Folders.Count == 0) {
        user.Folders.Add(new SWIFolder() { path = @"\Samples", name = "New Samples", right = 4, sql = true, manage = 0, expand = true });
    }
}

SecurityGroup.FolderDetailScript:
@{
    SecurityUser user = Model;
    
    //Full documentation at https://sealreport.org/Help/Index.html

    if (user.ScriptNumber == 1) {
        user.FolderDetail.files = new SWIFile[0];
    }

    //Add reports
    if (user.FolderDetail.folder.path == @"\Samples") {
        user.FolderDetail.files = user.FolderDetail.files.Concat(user.FolderDetailFiles.Where(i => i.name.Contains("Charts"))).ToArray(); 
    }
}
